<script>
  import { browser } from '$app/environment';
  import QRCode from 'qrcode';

  import {
    defaultImg,
    // formatId, fromGlobalId, loadImageToDataUri,
    loadAvatarDataUri
  } from '$lib/utils';

  // import { PUBLIC_CAMPAIGN_LINK } from '$env/static/public';

  export let userData;
  export let el;
  export let callForVoteText;
  export let campaignUrl;
  export let showAvatar;
  export let gesture;

  $: if (browser) {
    console.log('input:', {
      userData,
      callForVoteText,
      campaignUrl,
      showAvatar,
      gesture
    });
  }

  $: avatarImg = userData?.data?.user?.avatar ?? userData?.data?.avatar;
  let userImgDataUri;
  $: if (browser) {
    if (avatarImg) {
      loadAvatarDataUri(avatarImg).then((dataUri) => {
        userImgDataUri = dataUri;
      });
    }
  }

  $: displayName = (userData?.data?.user?.displayName || '').trim();

  let mattersQrCodeUri;
  $: if (browser && campaignUrl) {
    QRCode.toDataURL(campaignUrl, { margin: 0 }).then((datauri) => {
      mattersQrCodeUri = datauri;
    });
  }
</script>

